  `is_selected` tinyint(1) NULL DEFAULT 0 COMMENT '是否被用户选择',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `photo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '照片',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `travel_plan_id`(`travel_plan_id` ASC) USING BTREE,
  CONSTRAINT `accommodations_ibfk_1` FOREIGN KEY (`travel_plan_id`) REFERENCES `travel_plans` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT
