-- ----------------------------
-- Table structure for conversation_message
-- ----------------------------
DROP TABLE IF EXISTS `conversation_message`;
CREATE TABLE `conversation_message`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '消息ID',
  `session_id` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '会话标识符',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `message_type` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '消息类型：user(用户消息)、assistant(智能体消息)、system(系统消息)',
  `message_content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '消息内容',
  `message_role` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '消息角色：user、assistant、system',
  `sequence_number` int NOT NULL COMMENT '消息序号（在会话中的顺序）',
  `created_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '消息创建时间',
  `response_time` datetime NULL DEFAULT NULL COMMENT '响应时间（智能体消息的生成时间）',
  `token_count` int NULL DEFAULT NULL COMMENT 'Token数量',
  `model_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '使用的模型名称',
  `temperature` decimal(3, 2) NULL DEFAULT NULL COMMENT '模型温度参数',
  `status` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT 'normal' COMMENT '消息状态：normal(正常)、edited(已编辑)、deleted(已删除)、failed(失败)',
  `is_regenerated` tinyint(1) NULL DEFAULT 0 COMMENT '是否为重新生成的消息',
  `parent_message_id` bigint NULL DEFAULT NULL COMMENT '父消息ID（用于消息编辑和重新生成）',
  `user_rating` tinyint NULL DEFAULT NULL COMMENT '用户评分（1-5分）',
  `user_feedback` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL COMMENT '用户反馈内容',
  `context_data` json NULL COMMENT '上下文数据（JSON格式）',
  `attachments` json NULL COMMENT '附件信息（图片、文件等）',
  `metadata` json NULL COMMENT '其他元数据',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_session_id`(`session_id` ASC) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  INDEX `idx_session_sequence`(`session_id` ASC, `sequence_number` ASC) USING BTREE,
  INDEX `idx_created_time`(`created_time` ASC) USING BTREE,
  INDEX `idx_message_type`(`message_type` ASC) USING BTREE,
  INDEX `idx_parent_message`(`parent_message_id` ASC) USING BTREE,
  CONSTRAINT `conversation_message_ibfk_1` FOREIGN KEY (`parent_message_id`) REFERENCES `conversation_message` (`id`) ON DELETE SET NULL ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '对话消息表' ROW_FORMAT = DYNAMIC;

