INSERT INTO `travel_plans` VALUES (1, 1, '保定', 1, '保定一日游', 450.00, '建议穿着保暖衣物，明天保定天气较冷。驴肉火烧是保定特色，一定要品尝。市区交通便利，建议使用打车或公交出行。', '两人同行，单人出行注意保管好个人物品', 'draft', '2025-11-20 01:45:14', '2025-11-20 10:45:51', '2025-01-01', NULL, NULL, NULL);
INSERT INTO `travel_plans` VALUES (2, 1, '上海', 3, '上海三日特种兵旅行', 3250.00, '下载\'Metro大都会\'APP方便地铁出行，迪士尼和东方明珠建议提前预约，携带学生证享受优惠，轻装出行减轻负担，合理安排休息时间', '大学生特种兵旅行，紧凑高效行程，适合快速打卡上海经典地标', 'draft', '2025-11-20 02:40:13', '2025-11-20 02:40:13', '2025-01-01', NULL, NULL, NULL);
INSERT INTO `travel_plans` VALUES (3, 1, '北京', 2, '北京两日游', 1095.00, '推荐住在王府井或前门附近的酒店，交通便利且靠近主要景点。独自旅行注意保管好个人物品，下载\'北京一卡通\'APP方便乘坐公共交通。', '独自旅行，行程安排相对灵活，适合单人游览的景点和路线', 'draft', '2025-11-21 01:02:36', '2025-11-21 01:02:36', '2025-01-01', NULL, NULL, NULL);
INSERT INTO `travel_plans` VALUES (4, 3, '长沙', 2, '长沙两日游', 1740.00, '推荐住在五一广场附近，交通便利，周边商圈丰富', '情侣出行，偏好商圈购物和美食', 'draft', '2025-11-21 01:42:00', '2025-11-21 01:42:00', '2025-11-21', NULL, NULL, NULL);
INSERT INTO `travel_plans` VALUES (5, 4, '首尔', 2, '首尔两日购物游', 1410.00, '推荐住在明洞区域的酒店，购物极其便利。记得办理退税手续，单店消费满3万韩元可退税。建议携带空行李箱或预留购物打包预算。', '购物主题旅行，重点关注化妆品、时尚服饰购物，行程安排以购物区为主', 'draft', '2025-11-21 01:59:36', '2025-11-21 01:59:36', '2025-01-01', NULL, NULL, NULL);
INSERT INTO `travel_plans` VALUES (6, 4, '乌鲁木齐', 3, '乌鲁木齐三日游', 1895.00, '推荐住在市中心或二道桥附近的酒店，交通便利且靠近主要景点。12月乌鲁木齐气温较低，需准备保暖衣物。独自旅行注意保管好个人物品，尊重当地少数民族风俗习惯。', '独自旅行，行程安排相对灵活，重点关注新疆民族文化体验和自然风光欣赏', 'draft', '2025-11-21 02:22:13', '2025-11-21 11:03:21', '2025-12-04', NULL, NULL, NULL);
INSERT INTO `travel_plans` VALUES (7, 4, '海口', 2, '海口两日游', 1568.00, '推荐住在市中心或骑楼老街附近的酒店，交通便利且靠近主要景点。海口气候温暖湿润，注意防晒和防雨。独自旅行注意保管好个人物品，品尝海鲜选择正规餐厅。', '独自旅行，行程安排相对灵活，重点关注海口热带滨海风光和历史文化体验', 'active', '2025-11-21 03:21:25', '2025-11-21 12:12:20', '2025-01-01', NULL, NULL, NULL);
INSERT INTO `travel_plans` VALUES (8, 3, '新乡宝泉', 2, '新乡宝泉两日游', 1800.00, '推荐住在宝泉景区周边的民宿，环境优美且价格实惠，适合山水风光游览', '山水风光游览，包含宝泉大峡谷和崖天下两大核心景区', 'draft', '2025-11-21 04:12:01', '2025-11-21 04:12:01', NULL, NULL, '2025-11-23', '2025-11-22');
INSERT INTO `travel_plans` VALUES (9, 4, '上海', 2, '上海两日游', 1100.00, '推荐住在市中心区域，交通便利，便于游览各景点。下载\'上海地铁\'APP方便出行。', '无特殊需求', 'draft', '2025-11-24 06:10:47', '2025-11-24 06:10:47', NULL, NULL, '2025-11-25', '2025-11-24');
INSERT INTO `travel_plans` VALUES (10, 4, '三亚', 3, '三亚三日游', 1762.00, '推荐住在三亚湾或大东海区域，交通便利且餐饮选择丰富。元旦期间是旅游旺季，建议提前预订。', '注意防晒，准备泳衣和沙滩用品，建议购买旅游意外险', 'draft', '2025-11-24 09:32:56', '2025-11-24 09:32:56', NULL, NULL, '2025-01-03', '2025-01-01');
INSERT INTO `travel_plans` VALUES (11, 4, '天津', 2, '天津二日游', 1200.00, '推荐住在市中心区域，交通便利且餐饮选择丰富。1月份天津气温较低，务必准备保暖衣物。', '准备保暖衣物，建议购买地铁一日票，注意冬季防寒保暖', 'draft', '2025-11-24 09:54:14', '2025-11-24 09:54:14', NULL, NULL, '2026-01-05', '2026-01-04');
INSERT INTO `travel_plans` VALUES (12, 4, '延吉', 3, '延吉三日游', 2215.00, '推荐住在延吉市中心区域，交通便利且餐饮选择丰富。12月份延吉气温极低，务必准备厚羽绒服等保暖衣物。', '准备厚羽绒服、保暖内衣、帽子围巾手套等防寒装备，注意冬季防寒保暖', 'draft', '2025-11-24 10:02:45', '2025-11-24 10:02:45', NULL, NULL, '2025-12-08', '2025-12-06');

