INSERT INTO `trip_schemes` VALUES (1, '天津3日经典文化游', 3, '[{\"day\":1,\"theme\":\"欧式风情与历史建筑\",\"routes_used\":[\"五大道文化旅游区\",\"小白楼欧式风情\"],\"spots\":[\"天津五大道文化旅游区\",\"民园广场\",\"庆王府\",\"五大道公园\",\"小白楼\",\"小白楼1902欧式风情街\"],\"time_schedule\":\"上午：五大道区域游览 → 下午：小白楼欧式风情 → 晚上：津湾广场夜景\",\"highlights\":\"漫步五大道，感受万国建筑博览会的魅力，体验天津独特的欧陆风情\",\"photo\":\"http://store.is.autonavi.com/showpic/3138223e49d8298d3b62d0b2105acc6b\"},{\"day\":2,\"theme\":\"文化博览与自然休闲\",\"routes_used\":[\"博物馆文化之旅\",\"水上公园休闲游\"],\"spots\":[\"天津博物馆\",\"天津自然博物馆\",\"天津文化中心海底世界\",\"天津水上公园\",\"天津动物园\"],\"time_schedule\":\"上午：博物馆参观 → 下午：水上公园休闲 → 晚上：天塔湖夜景\",\"highlights\":\"深度了解天津历史文化，同时享受自然风光与休闲时光\",\"photo\":\"http://store.is.autonavi.com/showpic/abc650f219f65099c11c966ef4c7290b\"},{\"day\":3,\"theme\":\"名人故居与宗教建筑\",\"routes_used\":[\"名人故居巡礼\",\"意大利风情体验\"],\"spots\":[\"瓷房子\",\"张学良故居\",\"天主教天津教区西开总堂\",\"天津意大利风情旅游区\",\"周恩来邓颖超纪念馆\"],\"time_schedule\":\"上午：名人故居参观 → 下午：意大利风情区 → 晚上：人民公园休闲\",\"highlights\":\"探访历史名人足迹，感受多元文化交融的天津特色\",\"photo\":\"http://aos-cdn-image.amap.com/sns/ugccomment/3f36409c-8095-41a3-ae32-2f661142a871.jpg\"}]', '此行程全面覆盖天津历史文化、欧式建筑与自然风光，节奏舒缓，适合初次来津游客深度体验。');
INSERT INTO `trip_schemes` VALUES (2, '哈尔滨3日经典冰雪文化游', 3, '[{\"day\":1,\"theme\":\"城市地标与历史文化探索\",\"routes_used\":[\"中央大街历史文化游\",\"松花江畔风光游\"],\"spots\":[\"哈尔滨中央大街\",\"哈尔滨防洪纪念塔广场\",\"哈尔滨市人民防洪胜利纪念塔\",\"斯大林公园\",\"江上城堡观光索道\"],\"time_schedule\":\"上午：中央大街漫步 → 中午：中央大街午餐 → 下午：防洪纪念塔、斯大林公园 → 傍晚：江上索道观日落\",\"highlights\":\"体验哈尔滨最具代表性的欧式建筑风情，感受松花江畔的壮丽景色，了解城市历史文脉\",\"photo\":\"http://store.is.autonavi.com/showpic/55a8f05aa310f8cad7ca0ae6\"},{\"day\":2,\"theme\":\"太阳岛自然生态与极地探秘\",\"routes_used\":[\"太阳岛自然风光游\",\"极地动物观赏游\"],\"spots\":[\"太阳岛风景区\",\"哈尔滨极地公园\",\"哈尔滨极地公园·极地馆\",\"太阳岛大熊猫馆\",\"金河湾湿地植物园\"],\"time_schedule\":\"上午：太阳岛风景区 → 中午：景区内用餐 → 下午：极地公园+大熊猫馆 → 傍晚：湿地植物园漫步\",\"highlights\":\"近距离观赏北极动物与大熊猫，体验湿地生态，感受自然与动物的和谐共处\",\"photo\":\"http://store.is.autonavi.com/showpic/047b38e5d58349b5af2785b2a206f7a2\"},{\"day\":3,\"theme\":\"现代娱乐与传统文化体验\",\"routes_used\":[\"融创娱乐体验游\",\"巴洛克文化街区游\"],\"spots\":[\"哈尔滨融创乐园\",\"哈尔滨热雪奇迹\",\"银河欢乐世界\",\"中华巴洛克历史文化街区\",\"哈尔滨音乐公园\"],\"time_schedule\":\"上午：融创乐园/热雪奇迹 → 中午：融创文旅城用餐 → 下午：银河欢乐世界 → 傍晚：巴洛克街区+音乐公园\",\"highlights\":\"从现代主题乐园到传统历史街区，体验哈尔滨多元文化魅力，感受冰城现代与传统的完美融合\",\"photo\":\"http://store.is.autonavi.com/showpic/14a688744931748e3744df368b16b074\"}]', '此行程涵盖哈尔滨经典地标、自然风光与现代娱乐，节奏张弛有度，适合初次来哈游客全面体验冰城魅力。');
