-- ----------------------------
-- Table structure for user_center
-- ----------------------------
DROP TABLE IF EXISTS `user_center`;
CREATE TABLE `user_center`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '个人中心记录ID',
  `user_id` bigint NOT NULL COMMENT '用户ID（外键，关联user_info表的UserID）',
  `nickname` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '昵称',
  `real_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '真实姓名',
  `gender` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '性别：male/female/other/secret',
  `birthday` date NULL DEFAULT NULL COMMENT '生日',
  `bio` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '个人简介/签名',
  `location` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '所在地（省市区）',
  `hometown` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '家乡',
  `occupation` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '职业',
  `interests` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '兴趣爱好（逗号分隔）',
  `travel_style` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '旅行风格：solo/couple/family/friends/group',
  `favorite_destinations` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '喜爱的目的地（逗号分隔）',
  `post_count` int NULL DEFAULT 0 COMMENT '发布的帖子总数',
  `published_post_count` int NULL DEFAULT 0 COMMENT '已发布的帖子数',
  `draft_post_count` int NULL DEFAULT 0 COMMENT '草稿帖子数',
  `attraction_favorite_count` int NULL DEFAULT 0 COMMENT '收藏的景点数',
  `route_favorite_count` int NULL DEFAULT 0 COMMENT '收藏的路线数',
  `post_favorite_count` int NULL DEFAULT 0 COMMENT '收藏的帖子数',
  `route_history_count` int NULL DEFAULT 0 COMMENT '路线历史记录数',
  `total_likes_received` int NULL DEFAULT 0 COMMENT '收到的总点赞数',
  `total_comments_received` int NULL DEFAULT 0 COMMENT '收到的总评论数',
  `total_views_received` int NULL DEFAULT 0 COMMENT '帖子获得的总浏览量',
  `total_followers` int NULL DEFAULT 0 COMMENT '粉丝数（如果后续有关注功能）',
  `total_following` int NULL DEFAULT 0 COMMENT '关注数（如果后续有关注功能）',
  `level` int NULL DEFAULT 1 COMMENT '用户等级（1-10）',
  `experience_points` int NULL DEFAULT 0 COMMENT '经验值',
  `badges` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '获得的徽章（JSON格式）',
  `achievements` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '成就列表（JSON格式）',
  `preferred_travel_season` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '偏好的旅行季节：spring/summer/autumn/winter/all',
  `preferred_travel_type` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '偏好的旅行类型（逗号分隔）：culture/nature/adventure/food/photography等',
  `preferred_budget_range` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '偏好预算范围：budget/moderate/luxury',
  `notification_preferences` json NULL COMMENT '通知偏好设置（JSON格式）',
  `privacy_settings` json NULL COMMENT '隐私设置（JSON格式）',
  `show_real_name` tinyint(1) NULL DEFAULT 0 COMMENT '是否显示真实姓名',
  `show_birthday` tinyint(1) NULL DEFAULT 0 COMMENT '是否显示生日',
  `show_location` tinyint(1) NULL DEFAULT 1 COMMENT '是否显示所在地',
  `show_email` tinyint(1) NULL DEFAULT 0 COMMENT '是否显示邮箱',
  `show_phone` tinyint(1) NULL DEFAULT 0 COMMENT '是否显示手机号',
  `show_statistics` tinyint(1) NULL DEFAULT 1 COMMENT '是否显示统计数据',
  `show_favorites` tinyint(1) NULL DEFAULT 1 COMMENT '是否显示收藏内容',
  `show_posts` tinyint(1) NULL DEFAULT 1 COMMENT '是否显示发布的帖子',
  `cover_image` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '个人中心封面图片URL',
  `theme_color` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '主题颜色',
  `background_image` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '背景图片URL',
  `wechat` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '微信号',
  `qq` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT 'QQ号',
  `weibo` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '微博账号',
  `email` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '邮箱',
  `website` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '个人网站',
  `created_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `last_statistics_update` datetime NULL DEFAULT NULL COMMENT '统计数据最后更新时间',
  `last_active_time` datetime NULL DEFAULT NULL COMMENT '最后活跃时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uk_user_id`(`user_id` ASC) USING BTREE,
  INDEX `idx_nickname`(`nickname` ASC) USING BTREE,
  INDEX `idx_location`(`location` ASC) USING BTREE,
  INDEX `idx_level`(`level` ASC) USING BTREE,
  INDEX `idx_experience_points`(`experience_points` ASC) USING BTREE,
  INDEX `idx_created_time`(`created_time` ASC) USING BTREE,
  INDEX `idx_last_active_time`(`last_active_time` ASC) USING BTREE,
  CONSTRAINT `user_center_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user_info` (`UserID`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 6 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '用户个人中心表' ROW_FORMAT = DYNAMIC;

