-- ----------------------------
-- Procedure structure for update_user_center_statistics
-- ----------------------------
DROP PROCEDURE IF EXISTS `update_user_center_statistics`;
delimiter ;;
CREATE PROCEDURE `update_user_center_statistics`(IN p_user_id BIGINT)
BEGIN
  UPDATE `user_center` SET
    -- 帖子统计
    `post_count` = (
      SELECT COUNT(*) FROM `travel_post` 
      WHERE `publisher_id` = p_user_id AND `status` != 'deleted'
    ),
    `published_post_count` = (
      SELECT COUNT(*) FROM `travel_post` 
      WHERE `publisher_id` = p_user_id AND `status` = 'published'
    ),
    `draft_post_count` = (
      SELECT COUNT(*) FROM `travel_post` 
      WHERE `publisher_id` = p_user_id AND `status` = 'draft'
    ),
    -- 收藏统计
    `attraction_favorite_count` = (
      SELECT COUNT(*) FROM `attraction_favorite` 
      WHERE `user_id` = p_user_id AND (`is_valid` = 1 OR `is_valid` IS NULL)
    ),
    `route_favorite_count` = (
      SELECT COUNT(*) FROM `route_favorite` 
      WHERE `user_id` = p_user_id AND `is_valid` = 1
    ),
    `post_favorite_count` = (
      SELECT COUNT(*) FROM `travel_post_favorite` 
      WHERE `user_id` = p_user_id AND `status` = 'active' AND `is_deleted` = 0
    ),
    -- 路线历史统计
    `route_history_count` = (
      SELECT COUNT(*) FROM `route_history` 
      WHERE `user_id` = p_user_id
    ),
    -- 收到的互动统计
    `total_likes_received` = (
      SELECT COALESCE(SUM(`like_count`), 0) FROM `travel_post` 
      WHERE `publisher_id` = p_user_id AND `status` = 'published'
    ),
    `total_comments_received` = (
      SELECT COALESCE(SUM(`comment_count`), 0) FROM `travel_post` 
      WHERE `publisher_id` = p_user_id AND `status` = 'published'
    ),
    `total_views_received` = (
      SELECT COALESCE(SUM(`view_count`), 0) FROM `travel_post` 
      WHERE `publisher_id` = p_user_id AND `status` = 'published'
    ),
    -- 更新时间
    `last_statistics_update` = NOW()
  WHERE `user_id` = p_user_id;
END
;;
delimiter ;

