-- Triggers structure for table attraction_favorite
-- ----------------------------
DROP TRIGGER IF EXISTS `trg_update_user_center_on_attraction_favorite`;
delimiter ;;
CREATE TRIGGER `trg_update_user_center_on_attraction_favorite` AFTER INSERT ON `attraction_favorite` FOR EACH ROW BEGIN
  UPDATE `user_center` 
  SET `attraction_favorite_count` = `attraction_favorite_count` + 1,
      `last_statistics_update` = NOW()
  WHERE `user_id` = NEW.`user_id`;
END
;;
